(()=>{class t{constructor(t,e,n,o,l){this.title=t,this.description=e,this.priority=n,this.notes=o,this.date=l}}document.getElementById("content"),document.getElementById("task-form").addEventListener("submit",(function(e){e.preventDefault();const n=document.getElementById("title").value,o=document.getElementById("description").value,l=document.getElementById("priority").value,d=document.getElementById("notes").value,i=document.getElementById("date").value;let s=new t(n,o,l,d,i);return localStorage.setItem("newtask",JSON.stringify(s)),localStorage}))})();